package com.example.aplicatie_prajitura;

import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ListView;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import java.io.IOException;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.MalformedURLException;
import java.net.URL;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.Executor;
import java.util.concurrent.Executors;

public class ListaImaginiActivity extends AppCompatActivity {
private List<Bitmap>imagini = null;
private List<ImagineDomeniu>lista = null;
    @Override
    protected void onCreate(Bundle savedInstanceState) {


        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_lista_imagini);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        List<String> linkuriImagini = new ArrayList<>();
        //alba-ca-zapada
        linkuriImagini.add("https://www.google.com/imgres?q=prajitura%20alba%20ca%20zapada&imgurl=https%3A%2F%2Fjamilacuisine.ro%2Fwp-content%2Fuploads%2F2013%2F06%2FPrajitura-alba-ca-zapada.jpg&imgrefurl=https%3A%2F%2Fjamilacuisine.ro%2Fprajitura-alba-ca-zapada-reteta-video%2F&docid=4y6MNcF6Evm9mM&tbnid=CfiLrs-eRsAKCM&vet=12ahUKEwjnvq2apeaJAxXbVfEDHbXyGvAQM3oECBYQAA..i&w=1280&h=720&hcb=2&itg=1&ved=2ahUKEwjnvq2apeaJAxXbVfEDHbXyGvAQM3oECBYQAA");
        //tiramisu
        linkuriImagini.add("data:image/jpeg;base64,");
        //carrot-cake
        linkuriImagini.add("data:image/jpeg;base64,");
        //cheese-cake
        linkuriImagini.add("https://www.jocooks.com/wp-content/uploads/2018/11/cheesecake-1-22.jpg");
        //negresa
        linkuriImagini.add("https://jamilacuisine.ro/wp-content/uploads/2016/01/Negrese-glazurate-500x478.jpg");

        //pornim un fir paralel de executie
        Executor executor = Executors.newSingleThreadExecutor();
        //pentru intoarcere la firul principal folosim un handler
        Handler handler = new Handler(Looper.myLooper());

        executor.execute(new Runnable() {
            @Override
            public void run() {
                for(String link:linkuriImagini){
                    HttpURLConnection con = null;
                    try {
                        URL url = new URL(link);
                        con = (HttpURLConnection) url.openConnection();
                        InputStream is = con.getInputStream();
                        imagini.add(BitmapFactory.decodeStream(is));
                    } catch (MalformedURLException e) {
                        throw new RuntimeException(e);
                    } catch (IOException e) {
                        throw new RuntimeException(e);
                    }
                }
                handler.post(new Runnable() {
                    @Override
                    public void run() {
                        lista = new ArrayList<>();
                        lista.add(new ImagineDomeniu("Alba ca Zapada",imagini.get(0),"https://www.google.com/url?sa=i&url=https%3A%2F%2Fjamilacuisine.ro%2Fprajitura-alba-ca-zapada-reteta-video%2F&psig=AOvVaw3lGUxFRubXQTcm3AqjbPBV&ust=1732037585567000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCID8_Pm05okDFQAAAAAdAAAAABAE"));
                        lista.add(new ImagineDomeniu("Tiramisu",imagini.get(1),"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.kaufland.ro%2Fretete%2Fcauta-o-reteta%2Freteta.tiramisu-mascarpone-si-frisca-ou.r_id%3DRecipe_24028759.html&psig=AOvVaw3gU4VM_Azp7oCOegT1Bfwx&ust=1732037652332000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCMi4_pm15okDFQAAAAAdAAAAABAE"));
                        lista.add(new ImagineDomeniu("",imagini.get(2),"https://www.google.com/url?sa=i&url=https%3A%2F%2Fcooking.nytimes.com%2Frecipes%2F1015523-carrot-cake&psig=AOvVaw0PuDkWSYfnYLFrLKcf6eMf&ust=1732037717205000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCMD8pLm15okDFQAAAAAdAAAAABAE"));
                        lista.add(new ImagineDomeniu("",imagini.get(3),"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.jocooks.com%2Frecipes%2Fcheesecake-recipe%2F&psig=AOvVaw0HyyoQp0KzyX9OzRX5ifkV&ust=1732037733374000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCLCVwcC15okDFQAAAAAdAAAAABAE"));
                        lista.add(new ImagineDomeniu("",imagini.get(4),"https://www.google.com/url?sa=i&url=https%3A%2F%2Fjamilacuisine.ro%2Fnegrese-glazurate-reteta-video%2F&psig=AOvVaw0dupDEOsAo8YuXG-3cgvXx&ust=1732037744294000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCMiA4MW15okDFQAAAAAdAAAAABAE"));
                    }
                });

                ListView lv = findViewById(R.id.idLvImagini);
                ImagineAdapter adapter = new ImagineAdapter(lista, getApplicationContext(), R.layout.imagine_layout);
                lv.setAdapter(adapter);
            }
        });
        ListView lv = findViewById(R.id.idLvImagini);
        lv.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> adapterView, View view, int i, long l) {
                Intent it = new Intent(getApplicationContext(), WebViewActivity.class);
                it.putExtra("link",lista.get(i).getLink());
                startActivity(it);
            }
        });


    }
}